mapper.select:
SELECT business_day_id, business_date, is_active, created_at, updated_at
FROM business_days
WHERE business_date = #{businessDate}

SELECT business_day_id, business_date, is_active, created_at, updated_at
FROM business_days
WHERE business_day_id = #{businessDayId}

mapper.update:
UPDATE business_days
SET is_active = #{isActive}, updated_at = CURRENT_TIMESTAMP
WHERE business_day_id = #{businessDayId}
