mapper.select:
SELECT user_id, card_number, name, birth_date, phone_number, password, created_at, updated_at
FROM users
WHERE card_number = #{cardNumber}

SELECT user_id, card_number, name, birth_date, phone_number, password, created_at, updated_at
FROM users
WHERE card_number = #{cardNumber} AND phone_number = #{phoneNumber}

mapper.update:
UPDATE users
SET password = #{password}, updated_at = CURRENT_TIMESTAMP
WHERE card_number = #{cardNumber}
